package Exercicis;

/**
 * Exercici 15 
 * Repeteix l'exercici amb un do{}while().
 * @author: Rafel Sastre Mas.
 */


public class Exercici15 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int inici = 20;
		int fi = 100;

		do {
			System.out.println(inici);
			inici++;
		} while (inici <= fi);
		/*{
			System.out.println(inici);
			}*/
		
	}

}
